Affinity.GetCacheSafePath = function (path) {
    if (!Affinity.isie || typeOf(path) !== 'string') { return path; }
    return path + (path.contains('?') ? '&ran=' : '?ran=') + String.uniqueID() + '-' + Date.parse(new Date()).format('%s');
};

